import React from 'react'
import { useParams , Route} from 'react-router-dom'
import Comments from "../components/comments/Comments"
import HighlightedQuote from "../components/quotes/HighlightedQuote"

const  DUMMY_QUOTES = [
  {
    id: "q1", author: "Damla" , text:"Learning react is fun"
  },
  {
    id: "q2", author: "Damla Kara" , text:"Learning react is great"
  }
]

const QuoteDetail = () => {
   const params = useParams()
   console.log(params)
   console.log(params.quoteId)
   const paramQuoteId = params.quoteId

   const quote = DUMMY_QUOTES.find(quote => quote.id == paramQuoteId)
  return (
    <div>
        <HighlightedQuote  text={quote.text}   author={quote.author}/>
        <Route exact path={`/quotes/${paramQuoteId}/comments`}>
            <Comments />
        </Route>
    </div>
  )
}

export default QuoteDetail
